import React, { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import axios from 'axios';

const WinRateChart = () => {
  const [chartData, setChartData] = useState({});

  useEffect(() => {
    // Replace with your backend endpoint
    axios.get('https://your-backend-endpoint.com/api/winrates')
      .then(response => {
        const data = response.data;
        const timeLabels = data.map(item => item.time);
        const winRates = data.map(item => item.winRate);

        setChartData({
          labels: timeLabels,
          datasets: [
            {
              label: 'Win Rate Over Time',
              data: winRates,
              borderColor: 'rgba(75,192,192,1)',
              backgroundColor: 'rgba(75,192,192,0.2)',
              pointBackgroundColor: 'rgba(75,192,192,1)',
              pointBorderColor: '#fff',
              fill: true,
            },
          ],
        });
      })
      .catch(error => {
        console.error("There was an error fetching the win rates!", error);
      });
  }, []);

  return (
    <div>
      <h2>Win Rate Over Time</h2>
      <Line data={chartData} />
    </div>
  );
};

export default WinRateChart;
